package arrays;

public class Find_largestElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[]= {1,2,3,4,5};
		int first=a[0];
		for(int i=0;i<a.length;i++)
		{
			if(first>=a[i])
				System.out.println(a[i]);
			else if(a[i]>=a[i])
				System.out.println(a[i]);
		}

	}

}
